import { Link } from "react-router-dom";
import { Component } from "react";
import axios from "axios";
import Icon from "../../assets/icon/left.png";
import IconRight from "../../assets/icon/right.png";
import Cat from "../../Components/Cat/Cat";
import "./Cats.scss";

const origin = "http://localhost:9001";

class Cats extends Component {
  state = {
    catsList: [],
    chosenCat: {},
  };

  getChosenCatId() {
    if (this.props.match) {
      // if match exist in props, it hits /Chosen/:id
      return this.props.match.params.id;
    }
  }

  componentDidMount() {
    axios.get(`${origin}/cats`).then((response) => {
      this.setState({ catsList: response.data });
      const id = this.getChosenCatId();
      axios.get(`${origin}/cats/${id}`).then(({ data }) => {
        console.log(id);
        this.setState({ chosenCat: data });
      });
    });
  }
  render() {
    const { catsList } = this.state;
    const allCatsList = catsList.filter((cat) => {
      return cat.id !== "12";
    });
    return (
      <div className="catss">
        <div className="catss__nav">
          <Link to={`/`} className="catss__link">
            <img src={Icon} className="catss__icon" />
            Back
          </Link>
          <Link to={`/cats/add`} className="catss__link">
            Upload
            <img src={IconRight} className="catss__icon" />
          </Link>
        </div>

        <ul className="catss__cats">
          {allCatsList.map((cat) => {
            return (
              <li className="cats__cat" key={cat.id}>
                <Cat
                  id={cat.id}
                  name={cat.name}
                  image={cat.image}
                  age={cat.age}
                />
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}
export default Cats;
