import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import "./CatAdd.scss";

const origin = "http://localhost:9001";

function CatAdd() {
  const catBreeds = [
    "Please Choose Breed",
    "Domestic Shorthair",
    "American Shorthair",
    "British Shorthair",
    "Exotic Shorthair",
    "Egyptian Mau",
    "Havana",
    "Japanese Bobtail",
    "Himalayan",
    "Javanese",
    "Korat",
    "LaPerm",
    "Manx",
    "Munchkin",
    "others",
  ];

  const [data, setData] = useState({
    name: "",
    age: "",
    gender: "",
    health: "",
    personality: "",
    email: "",
    breed: "",
    city: "",
    image: null,
  });

  const inputHandler = (e) => {
    const newData = { ...data };
    newData[e.target.name] = e.target.value;
    setData(newData);
  };
  const selectHandler = (e) => {
    const selectedBreed = e.target;

    if (selectedBreed !== "Please Choose Breed") {
      selectedBreed.style.color = "black";
      selectedBreed.style.borderColor = "green";
    }
  };
  const onEmailHandler = (e) => {
    const regx = /^([a-z\d\.-]+)@([a-z\d-]+)\.([a-z]{2,8})(\.[a-z]{2,8})?$/;

    const email = e.target.value;
    console.log(email);

    if (regx.test(email)) {
      alert("Email is valid");
      return true;
    } else {
      alert("please enter valid email address!");
      return false;
    }
  };

  const fileSelectedHandler = (e) => {
    setData({ ...data, image: e.target.files[0] });
  };

  const submitHandler = (e) => {
    e.preventDefault();

    const fd = new FormData();
    fd.append("image", data.image, data.image.name);
    axios.post("", fd, {
      onUploadProgress: (ProgressEvent) => {
        console.log(
          "Upload Progress:" +
            Math.round(ProgressEvent.loaded / ProgressEvent.total) * 100
        );
      },
    });

    axios
      .post(`${origin}/cats`, {
        name: data.name,
        age: data.age,
        gender: data.gender,
        health: data.health,
        personality: data.personality,
        email: data.email,
        breed: data.breed,
        city: data.city,
      })
      .then((response) => {
        console.log(response.data);
      });
  };

  return (
    <div className="add">
      <form onSubmit={submitHandler} className="add__form">
        <input type="file" onChange={fileSelectedHandler} />

        <div className="add__labels">
          <label className="add__gdr">Gender</label>
          <div className="add__labelss">
            <section className="add__radio">
              <label htmlFor="gender">Boy</label>
              <input
                value={data.gender}
                type="radio"
                name="gender"
                id="gender"
                onChange={inputHandler}
              />
            </section>
            <section className="add__radio">
              <label>Girl</label>
              <input
                value={data.gender}
                type="radio"
                name="gender"
                id="gender"
                onChange={inputHandler}
                checked
              />
            </section>
          </div>
        </div>
        <div className="add__labels">
          <label htmlFor="name">Name</label>
          <input
            value={data.name}
            type="text"
            name="name"
            id="name"
            placeholder="Meow's Name"
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="age">Age</label>

          <input
            autoComplete="new-password"
            value={data.age}
            type="number"
            name="age"
            placeholder="please enter a number "
            min="0"
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="breed">Breed</label>

          <select
            className="add__input add__input--select"
            onChange={selectHandler}
          >
            {catBreeds.map((breed, index) => {
              return <option key={index}>{breed}</option>;
            })}
          </select>
        </div>

        <div className="add__labels">
          <label htmlFor="personality">Personality</label>
          <input
            autoComplete="new-password"
            value={data.personality}
            type="text"
            name="personality"
            id="personality"
            placeholder="Meow's Personality "
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="health">Health</label>
          <input
            autoComplete="new-password"
            value={data.health}
            type="text"
            name="health"
            id="health"
            placeholder="Health Situation "
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="city">City</label>
          <input
            autoComplete="new-password"
            value={data.city}
            type="text"
            name="city"
            id="city"
            placeholder="City Found "
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="email">Email</label>
          <input
            autoComplete="new-password"
            value={data.email}
            type="email"
            name="email"
            id="email"
            placeholder="me@example.com"
            className="add__input"
            onChange={onEmailHandler}
            required
          />
        </div>

        <button type="submit" className="add__btn">
          <Link className="add__link" to="/cats">
            Upload Meow
          </Link>
        </button>
      </form>
    </div>
  );
}
export default CatAdd;
