import "./Cat.scss";
import { Link } from "react-router-dom";
import axios from "axios";

const origin = "http://localhost:9001";

function Cat({ id, name, image, age, deleteCat }) {
  const deleteHandler = (id) => {
    const data = {
      id,
      name,
      age,
    };

    axios
      .delete(`${origin}/cats/${id}`, data)
      .then((data) => {
        console.log(data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className="cat">
      <Link className="cat__link" to={`/cats/${id}`}>
        <img className="cat__image" src={image} />
        <div className="cat__name">
          <h1 className="cat__label">{name}</h1>
          <h2 className="cat__age">{age}</h2>
        </div>

        <button
          onClick={() => {
            deleteHandler(id);
          }}
        >
          Remove {name}
        </button>
      </Link>
      <button onClick={() => props.deleteCat(id)}>Add To Adopt List</button>
    </div>
  );
}
export default Cat;
