import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import "./CatAdd.scss";

const origin = "http://localhost:9001";

function CatAdd() {
  const [data, setData] = useState({
    name: "",
    age: "",
    gender: "",
    health: "",
    personality: "",
    email: "",
    breed: "",
    city: "",
  });

  const errorMsgHandler = () => {
    alert("please enter information correctly");
  };

  const inputHandler = (e) => {
    const newData = { ...data };

    newData[e.target.name] = e.target.value;
    setData(newData);
  };

  const submitHandler = (e) => {
    e.preventDefault();

    axios
      .post(`${origin}/cats`, {
        name: data.name,
        age: data.age,
        gender: data.gender,
        health: data.health,
        personality: data.personality,
        email: data.email,
        breed: data.breed,
        city: data.city,
      })
      .then((response) => {
        console.log(response.data);
      });
  };

  return (
    <div className="add">
      <form onSubmit={submitHandler} className="add__form">
        <div className="add__labels">
          <label htmlFor="name">Name</label>
          <input
            value={data.name}
            type="text"
            name="name"
            id="name"
            placeholder="Meow's Name"
            className="add__input"
            onChange={inputHandler}
            onInvalid={errorMsgHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="age">Age</label>
          <input
            autoComplete="new-password"
            value={data.age}
            type="number"
            name="age"
            autoComplete="off"
            id="age"
            placeholder="please enter a number "
            min="0"
            className="add__input"
            onChange={inputHandler}
            onInvalid={errorMsgHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="breed">Breed</label>
          <input
            autoComplete="new-password"
            value={data.breed}
            type="text"
            name="breed"
            id="breed"
            placeholder="Breed"
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="personality">Personality</label>
          <input
            autoComplete="new-password"
            value={data.personality}
            type="text"
            name="personality"
            id="personality"
            placeholder="Meow's Personality "
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="health">Health</label>
          <input
            autoComplete="new-password"
            value={data.health}
            type="text"
            name="health"
            id="health"
            placeholder="Health Situation "
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="gender">Gender</label>
          <input
            autoComplete="new-password"
            value={data.gender}
            type="text"
            name="gender"
            id="gender"
            placeholder="Gender "
            className="add__input"
            onChange={inputHandler}
          />
        </div>

        <div className="add__labels">
          <label htmlFor="city">City</label>
          <input
            autoComplete="new-password"
            value={data.city}
            type="text"
            name="city"
            id="city"
            placeholder="City Found "
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <label htmlFor="email">Email</label>
          <input
            autoComplete="new-password"
            value={data.email}
            type="email"
            name="email"
            id="email"
            placeholder="Your Email Address"
            className="add__input"
            onChange={inputHandler}
            required
          />
        </div>

        <button type="submit" className="add__btn">
          <Link className="add__link" to="/cats">
            Upload Meow
          </Link>
        </button>
      </form>
    </div>
  );
}
export default CatAdd;
