import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import "./CatAdd.scss";

const origin = "http://localhost:9001";

const schema = yup.object().shape({
  name: yup.string().required("Name is required please"),
  age: yup.number().positive().integer().required("Age is required please"),
  gender: yup.string().required("Gender is required please"),
  health: yup.string().required("Health situation is required please"),
  personality: yup.string().required("Personality is required please"),
  email: yup.string().email().required("Email is required please"),
  breed: yup.string().required("Breed is required please"),
  city: yup.string().required("City is required please"),
});

function CatAdd() {
  // const {
  //   register,
  //   handleSubmit,
  //   formState: { errors },
  // } = useForm({
  //   resolver: yupResolver(schema),
  // });

  const [data, setData] = useState({
    name: "",
    age: "",
    gender: "",
    health: "",
    personality: "",
    email: "",
    breed: "",
    city: "",
  });

  const beforeSubmit = (e) => {
    // const nameInput = e.target.parentElement.name;
    // if (nameInput.validity.valueMissing) {
    //   nameInput.setCustomValidity("Please enter correctly... ");
    //   nameInput.reportValidity();
    // } else {
    //   nameInput.setCustomValidity("");
    // }
  };

  const inputHandler = (e) => {
    const newData = { ...data };

    newData[e.target.name] = e.target.value;
    setData(newData);

    // const nameInput = e.target;
    // if (nameInput.validity.valueMissing) {
    //   nameInput.setCustomValidity("Please enter correctly... ");
    //   nameInput.reportValidity();
    // } else {
    //   nameInput.setCustomValidity("");
    // }
  };

  const submitHandler = (e) => {
    e.preventDefault();

    axios
      .post(`${origin}/cats`, {
        name: data.name,
        age: data.age,
        gender: data.gender,
        health: data.health,
        personality: data.personality,
        email: data.email,
        breed: data.breed,
        city: data.city,
      })
      .then((response) => {
        console.log(response.data);
      });
  };

  return (
    <div className="add">
      {/* <form onSubmit={handleSubmit(submitHandler)} className="add__form"> */}
      <form onSubmit={submitHandler} className="add__form">
        <div className="add__labels">
          <label htmlFor="name">Name</label>
          <input
            autoComplete="new-password"
            value={data.name}
            type="text"
            name="name"
            id="name"
            placeholder="Meow's Name"
            className="add__input"
            // {...register("name", { required: true })}
            onChange={inputHandler}
            required
          />

          {/* {errors.message && errors.message.message} */}
        </div>

        <div className="add__labels">
          <label htmlFor="age">Age</label>
          <input
            value={data.age}
            type="number"
            name="age"
            autoComplete="off"
            id="age"
            placeholder="please enter a number "
            min="0"
            className="add__input"
            // {...register("age", { required: true })}
            onChange={inputHandler}
            required
          />

          {/* {errors.message && errors.message.message} */}
        </div>

        <div className="add__labels">
          <label htmlFor="breed">Breed</label>
          <input
            value={data.breed}
            type="text"
            name="breed"
            id="breed"
            placeholder="Breed"
            className="add__input"
            // {...register("breed", { required: true })}
            onChange={inputHandler}
            required
          />

          {/* {errors.message && errors.message.message} */}
        </div>

        <div className="add__labels">
          <label htmlFor="personality">Personality</label>
          <input
            value={data.personality}
            type="text"
            name="personality"
            id="personality"
            placeholder="Meow's Personality "
            className="add__input"
            // {...register("personality", { required: true })}
            onChange={inputHandler}
            required
          />

          {/* {errors.message && errors.message.message} */}
        </div>

        <div className="add__labels">
          <label htmlFor="health">Health</label>
          <input
            value={data.health}
            type="text"
            name="health"
            id="health"
            placeholder="Health Situation "
            className="add__input"
            // {...register("health", { required: true })}
            onChange={inputHandler}
            required
          />

          {/* {errors.message && errors.message.message} */}
        </div>

        <div className="add__labels">
          <label htmlFor="gender">Gender</label>
          <input
            value={data.gender}
            type="text"
            name="gender"
            id="gender"
            placeholder="Gender "
            className="add__input"
            // {...register("gender", { required: true })}
            onChange={inputHandler}
          />

          {/* {errors.message && errors.message.message} */}
        </div>

        <div className="add__labels">
          <label htmlFor="city">City</label>
          <input
            value={data.city}
            type="text"
            name="city"
            id="city"
            placeholder="City Found "
            className="add__input"
            //  {...register("city", { required: true })}
            onChange={inputHandler}
            required
          />

          {/* {errors.message && errors.message.message} */}
        </div>

        <div className="add__labels">
          <label htmlFor="email">Email</label>
          <input
            value={data.email}
            type="email"
            name="email"
            id="email"
            placeholder="Your Email Address"
            className="add__input"
            //  {...register("email", { required: true })}
            onChange={inputHandler}
            required
          />

          {/* {errors.message && errors.message.message} */}
        </div>

        <button type="submit" className="add__btn" onClick={beforeSubmit}>
          <Link className="add__link" to="/cats">
            Upload Meow
          </Link>
        </button>
      </form>
    </div>
  );
}
export default CatAdd;
