import { Link } from "react-router-dom";
import { Component } from "react";
import axios from "axios";
import Icon from "../../assets/icon/left.png";
import IconRight from "../../assets/icon/right.png";
import Cat from "../../Components/Cat/Cat";
import CatsToBeAdopted from "../CatToBeAdopted/CatsToBeAdopted";
import "./Cats.scss";

const origin = "http://localhost:9001";

class Cats extends Component {
  constructor() {
    super();
    this.state = {
      catsList: [],
      filterData: [],
      age: JSON.parse(localStorage.getItem("ageFilteredList")),
    };
  }
  getData() {
    this.setState({ age: JSON.parse(localStorage.getItem("ageFilteredList")) });
  }
  componentDidMount() {
    this.getData();
    axios.get(`${origin}/cats`).then((response) => {
      this.setState({ catsList: response.data });
    });

    let read = JSON.parse(localStorage.getItem("ageFilteredList"));

    if (read) {
      this.setState({
        filterData: read,
      });
    }
  }

  onAgeChanged = (e) => {
    if (e.target.value === "olderThan1") {
      var filteredData = this.state.catsList.filter((data) => {
        return data.age >= 1;
      });
    }
    if (e.target.value === "lessThan1") {
      var filteredData = this.state.catsList.filter((data) => {
        return data.age === "Less than 1 year old";
      });
    }

    localStorage.setItem("ageFilteredList", JSON.stringify(filteredData));

    this.setState({
      ...this.state,
      filterData: filteredData,
    });
  };

  render() {
    return (
      <div className="catss">
        <h4>Filtered Number: {this.state.filterData.length}</h4>

        <section>
          <label htmlFor="olderThan1">Months old</label>
          <input
            checked
            type="radio"
            name="age"
            id="olderThan1"
            value="olderThan1"
            onChange={(e) => {
              this.onAgeChanged(e);
            }}
          />

          <label htmlFor="lessThan1">1/Years old</label>
          <input
            type="radio"
            name="age"
            id="lessThan1"
            value="lessThan1"
            onChange={(e) => {
              this.onAgeChanged(e);
            }}
          />
        </section>

        <div className="catss__nav"></div>

        <ul className="catss__cats">
          {this.state.filterData.map((cat) => {
            return (
              <li className="cats__cat" key={cat.id}>
                <Cat
                  id={cat.id}
                  name={cat.name}
                  image={cat.image}
                  age={cat.age}
                  getData={this.chosenCat}
                />
              </li>
            );
          })}
        </ul>
        <CatsToBeAdopted />
      </div>
    );
  }
}
export default Cats;
