import { Link } from "react-router-dom";
import { Component } from "react";
import axios from "axios";
import Icon from "../../assets/icon/left.png";
import IconRight from "../../assets/icon/right.png";
import Cat from "../../Components/Cat/Cat";
import CatsToBeAdopted from "../CatToBeAdopted/CatsToBeAdopted";
import "./Cats.scss";

const origin = "http://localhost:9001";

class Cats extends Component {
  userData;

  state = {
    catsList: [],
    filterData: [],
  };

  componentDidMount() {
    this.userData = JSON.parse(localStorage.getItem("user"));

    axios.get(`${origin}/cats`).then((response) => {
      this.setState({ catsList: response.data });
    });
  }

  componentWillUpdate(nextProps, nextState) {
    localStorage.setItem("user", JSON.stringify(nextState));
  }

  onAgeChanged = (e) => {
    if (e.target.value === "olderThan1") {
      var filteredData = this.state.catsList.filter((data) => {
        return data.age >= 1;
      });
    }
    if (e.target.value === "lessThan1") {
      var filteredData = this.state.catsList.filter((data) => {
        return data.age === "Less than 1 year old";
      });
    }

    this.setState({
      ...this.state,
      filterData: filteredData,
    });
  };

  render() {
    return (
      <div className="catss">
        <h4>Filtered Number: {this.state.filterData.length}</h4>

        <section>
          <label htmlFor="olderThan1">Months old</label>
          <input
            type="radio"
            name="age"
            id="olderThan1"
            value="olderThan1"
            onChange={(e) => {
              this.onAgeChanged(e);
            }}
          />

          <label htmlFor="lessThan1">1/Years old</label>
          <input
            type="radio"
            name="age"
            id="lessThan1"
            value="lessThan1"
            onChange={(e) => {
              this.onAgeChanged(e);
            }}
          />
        </section>

        <div className="catss__nav">
          <Link to={`/`} className="catss__link">
            <img src={Icon} className="catss__icon" />
            Back
          </Link>

          <Link to={`/cats/add`} className="catss__link">
            Upload
            <img src={IconRight} className="catss__icon" />
          </Link>
        </div>

        <ul className="catss__cats">
          {this.state.filterData.map((cat) => {
            return (
              <li className="cats__cat" key={cat.id}>
                <Cat
                  id={cat.id}
                  name={cat.name}
                  image={cat.image}
                  age={cat.age}
                  getData={this.chosenCat}
                />
              </li>
            );
          })}
        </ul>
        <CatsToBeAdopted />
      </div>
    );
  }
}
export default Cats;
