import "./Cat.scss";
import { Link } from "react-router-dom";
import axios from "axios";

const origin = "http://localhost:9001";

function Cat({ id, name, image, age }) {
  const deleteHandler = (id) => {
    // fetch(`${origin}/cats/${id}`, {
    //   method: "DELETE",
    // }).then((result) => {
    //   result.json().then((res) => {
    //     console.warn(res);
    //   });
    // });

    const data = {
      id,
      name,
      age,
    };

    axios.delete(`${origin}/cats/${id}`);
  };

  return (
    <div className="cat">
      <Link className="cat__link" to={`/cats/${id}`}>
        <img className="cat__image" src={image} />
        <div className="cat__name">
          <h1 className="cat__label">{name}</h1>
          <h2 className="cat__age">{age}</h2>
        </div>
        <button
          onClick={() => {
            deleteHandler(id);
          }}
        >
          Remove Meow
        </button>
      </Link>
    </div>
  );
}
export default Cat;
