import axios from "axios";
import { Link, useHistory } from "react-router-dom";
import "./CatAdd.scss";

const origin = "http://localhost:9001";

function CatAdd() {
  // useHistory can only be used in functional component
  // can only use useHistory hook to manually change router instead of using Link
  const history = useHistory();

  const uploadCat = (e) => {
    e.prevent.default();

    axios
      .post(`${origin}/cats`, {
        name: e.target.catName.value,
      })
      .then(
        (response) => {
          // only navigate to the newly uploaded cat detail page when successfully uploaded
          history.push(`/cats/${response.data.id}`);
          alert("Meow is successfully added to the list !");
        },
        (error) => {
          alert(error.message);
        }
      );
  };

  return (
    <div>
      <form onSubmit={uploadCat}>
        <img />
        <label>Name</label>
        <input name="catName" />
        <label>Age</label>
        <input name="gender" />
        <label>Gender</label>
        <label>Personality</label>
        <label>City</label>
        <label>Contact</label>
        <button type="submit">Add Meow</button>
      </form>
    </div>
  );
}
export default CatAdd;
