import axios from "axios";
import { Component } from "react";
import { Link } from "react-router-dom";
import "./CatAdd.scss";

const origin = "http://localhost:9001";

class CatAdd extends Component {
  state = {
    catName: "",
    age: "",
    gender: "",
    breed: "",
    health: "",
    personality: "",
    city: "",
    contact: "",
  };

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = (e) => {
    e.preventDefault();

    axios
      .post(`${origin}/cats`, this.state)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    const { catName, age, gender, breed, health, personality, city, contact } =
      this.state;
    return (
      <div className="add">
        <form onSubmit={this.submitHandler} className="add__form">
          <input
            type="text"
            name="catName"
            value={catName}
            onChange={this.changeHandler}
          />

          <input
            type="text"
            name="age"
            value={age}
            onChange={this.changeHandler}
          />

          <input
            type="text"
            name="breed"
            value={breed}
            onChange={this.changeHandler}
          />

          <input
            type="text"
            name="personality"
            value={personality}
            onChange={this.changeHandler}
          />

          <input
            type="text"
            name="health"
            value={health}
            onChange={this.changeHandler}
          />

          <input
            type="text"
            name="gender"
            value={gender}
            onChange={this.changeHandler}
          />

          <input
            type="text"
            name="city"
            value={city}
            onChange={this.changeHandler}
          />

          <input
            type="text"
            name="contact"
            value={contact}
            onChange={this.changeHandler}
          />

          <button type="submit">Add Meow</button>
        </form>
      </div>
    );
  }
}
export default CatAdd;
