import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import "./CatAdd.scss";

const origin = "http://localhost:9001";

const schema = yup.object().shape({
  name: yup.string().required(),
  age: yup.number().positive().integer().required(),
  gender: yup.string().required(),
  health: yup.string().required(),
  personality: yup.string().required(),
  email: yup.string().email().required(),
  breed: yup.string().required(),
  city: yup.string().required(),
});

function CatAdd() {
  const [data, setData] = useState({
    name: "",
    age: "",
    gender: "",
    health: "",
    personality: "",
    email: "",
    breed: "",
    city: "",
  });

  const inputHandler = (e) => {
    const newData = { ...data };
    newData[e.target.name] = e.target.value;
    setData(newData);
  };

  const Form = () => {
    const { register, handleSubmit, errors } = useForm({
      resolver: yupResolver(schema),
    });
  };

  const submitHandler = (e) => {
    e.preventDefault();

    axios
      .post(`${origin}/cats`, {
        name: data.name,
        age: data.age,
        gender: data.gender,
        health: data.health,
        personality: data.personality,
        email: data.email,
        breed: data.breed,
        city: data.city,
      })
      .then((response) => {
        console.log(response.data);
      });
  };

  return (
    <div className="add">
      <form onSubmit={submitHandler} className="add__form">
        <div className="add__labels">
          <label htmlFor="name">Name</label>
          <input
            value={data.name}
            type="text"
            name="name"
            placeholder="Meow's Name"
            className={`add__input ${
              data.name === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
          />
        </div>

        <div className="add__labels">
          <label htmlFor="age">Age</label>
          <input
            value={data.age}
            type="text"
            name="age"
            placeholder="Meow's Age "
            className={`add__input ${
              data.age === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
          />
        </div>

        <div className="add__labels">
          <label htmlFor="breed">Breed</label>
          <input
            value={data.breed}
            type="text"
            name="breed"
            placeholder="Breed"
            className={`add__input ${
              data.breed === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
          />
        </div>

        <div className="add__labels">
          <label htmlFor="personality">Personality</label>
          <input
            value={data.personality}
            type="text"
            name="personality"
            placeholder="Meow's Personality "
            className={`add__input ${
              data.personality === ""
                ? "add__input--error"
                : "add__input--success"
            }`}
            onChange={inputHandler}
          />
        </div>

        <div className="add__labels">
          <label htmlFor="health">Health</label>
          <input
            value={data.health}
            type="text"
            name="health"
            placeholder="Health Situation "
            className={`add__input ${
              data.health === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
          />
        </div>

        <div className="add__labels">
          <label htmlFor="gender">Gender</label>
          <input
            value={data.gender}
            type="text"
            name="gender"
            placeholder="Gender "
            className={`add__input ${
              data.gender === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
          />
        </div>

        <div className="add__labels">
          <label htmlFor="city">City</label>
          <input
            value={data.city}
            type="text"
            name="city"
            placeholder="City Found "
            className={`add__input ${
              data.city === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
          />
        </div>

        <div className="add__labels">
          <label htmlFor="email">Email</label>
          <input
            value={data.email}
            type="email"
            name="email"
            placeholder="Your Email Address"
            className={`add__input ${
              data.email === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
          />
        </div>

        <button type="submit" className="add__btn">
          <Link className="add__link" to="/cats">
            Upload Meow
          </Link>
        </button>
      </form>
    </div>
  );
}
export default CatAdd;
