import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import "./CatAdd.scss";

const origin = "http://localhost:9001";

const schema = yup.object().shape({
  name: yup.string().required("Name is required please"),
  age: yup.number().positive().integer().required("Age is required please"),
  gender: yup.string().required("Gender is required please"),
  health: yup.string().required("Health situation is required please"),
  personality: yup.string().required("Personality is required please"),
  email: yup.string().email().required("Email is required please"),
  breed: yup.string().required("Breed is required please"),
  city: yup.string().required("City is required please"),
});

function CatAdd() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const [data, setData] = useState({
    name: "",
    age: "",
    gender: "",
    health: "",
    personality: "",
    email: "",
    breed: "",
    city: "",
  });

  const inputHandler = (e) => {
    const newData = { ...data };
    newData[e.target.name] = e.target.value;
    setData(newData);
  };

  const submitHandler = (e) => {
    e.preventDefault();

    axios
      .post(`${origin}/cats`, {
        name: data.name,
        age: data.age,
        gender: data.gender,
        health: data.health,
        personality: data.personality,
        email: data.email,
        breed: data.breed,
        city: data.city,
      })
      .then((response) => {
        console.log(response.data);
      });
  };

  return (
    <div className="add">
      <form onSubmit={handleSubmit(submitHandler)} className="add__form">
        <div className="add__labels">
          <label htmlFor="name">Name</label>
          <input
            value={data.name}
            type="text"
            name="name"
            placeholder="Meow's Name"
            className={`add__input ${
              data.name === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            {...register("name", { required: true })}
          />
          {/* <p>{errors.name?.message}</p> */}
          {errors.message && errors.message.message}
        </div>

        <div className="add__labels">
          <label htmlFor="age">Age</label>
          <input
            value={data.age}
            type="text"
            name="age"
            placeholder="Meow's Age "
            className={`add__input ${
              data.age === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            {...register("age", { required: true })}
          />
          {/* <p>{errors.age?.message}</p> */}
          {errors.message && errors.message.message}
        </div>

        <div className="add__labels">
          <label htmlFor="breed">Breed</label>
          <input
            value={data.breed}
            type="text"
            name="breed"
            placeholder="Breed"
            className={`add__input ${
              data.breed === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            {...register("breed", { required: true })}
          />
          {/* <p>{errors.breed?.message}</p> */}
          {errors.message && errors.message.message}
        </div>

        <div className="add__labels">
          <label htmlFor="personality">Personality</label>
          <input
            value={data.personality}
            type="text"
            name="personality"
            placeholder="Meow's Personality "
            className={`add__input ${
              data.personality === ""
                ? "add__input--error"
                : "add__input--success"
            }`}
            onChange={inputHandler}
            {...register("personality", { required: true })}
          />
          {/* <p>{errors.personality?.message}</p> */}
          {errors.message && errors.message.message}
        </div>

        <div className="add__labels">
          <label htmlFor="health">Health</label>
          <input
            value={data.health}
            type="text"
            name="health"
            placeholder="Health Situation "
            className={`add__input ${
              data.health === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            {...register("health", { required: true })}
          />
          {/* <p>{errors.health?.message}</p> */}
          {errors.message && errors.message.message}
        </div>

        <div className="add__labels">
          <label htmlFor="gender">Gender</label>
          <input
            value={data.gender}
            type="text"
            name="gender"
            placeholder="Gender "
            className={`add__input ${
              data.gender === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            {...register("gender", { required: true })}
          />
          {/* <p>{errors.gender?.message}</p> */}
          {errors.message && errors.message.message}
        </div>

        <div className="add__labels">
          <label htmlFor="city">City</label>
          <input
            value={data.city}
            type="text"
            name="city"
            placeholder="City Found "
            className={`add__input ${
              data.city === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            {...register("city", { required: true })}
          />
          {/* <p>{errors.city?.message}</p> */}
          {errors.message && errors.message.message}
        </div>

        <div className="add__labels">
          <label htmlFor="email">Email</label>
          <input
            value={data.email}
            type="email"
            name="email"
            placeholder="Your Email Address"
            className={`add__input ${
              data.email === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            {...register("email", { required: true })}
          />
          {/* <p>{errors.email?.message}</p> */}
          {errors.message && errors.message.message}
        </div>

        <button type="submit" className="add__btn">
          <Link className="add__link" to="/cats">
            Upload Meow
          </Link>
        </button>
      </form>
    </div>
  );
}
export default CatAdd;
