import { Component } from "react";
import axios from "axios";
import "./CatDetails.scss";

const origin = "http://localhost:9001";

class CatDetails extends Component {
  state = {
    CatDetails: {},
    error: undefined,
  };

  getMainCatId() {
    if (this.props.match) {
      // if match exist in props, it hits /cats/:id
      return this.props.match.params.id;
    }
  }

  componentDidMount() {
    const id = this.getMainCatId();
    if (!id) {
      this.setState({ CatDetails: {} });
      return;
    }
    axios.get(`${origin}/cats/${id}`).then((response) => {
      console.log(response);
      this.setState({
        CatDetails: response.data,
      });
    });
  }
  render() {
    const {
      name,
      age,
      breed,
      personality,
      health,
      gender,
      contactFounder,
      city,
      image,
    } = this.state.CatDetails;

    return (
      <div className="details">
        <img src={image} />
        <span>{name}</span>
        <span>{age}</span>
        <span>{breed}</span>
        <span>{personality}</span>
        <span>{health}</span>
        <span>{gender}</span>
        <span>{city}</span>
        <p>{contactFounder}</p>
      </div>
    );
  }
}
export default CatDetails;
