import { Link } from "react-router-dom";
import { Component } from "react";
import axios from "axios";
import Icon from "../../assets/icon/left.png";
import IconRight from "../../assets/icon/right.png";
import Cat from "../../Components/Cat/Cat";
import CatsToBeAdopted from "../CatToBeAdopted/CatsToBeAdopted";
import "./Cats.scss";

const origin = "http://localhost:9001";

class Cats extends Component {
  constructor() {
    super();
    this.state = {
      catsList: [],
      filterData: [],
      age: "olderThan1",
    };
  }
  initialData = () => {
    if (this.state.age === "olderThan1") {
      var olderThan1 = this.state.catsList.filter((data) => {
        return data.age >= 1;
      });
      this.setState({ ...this.state, filterData: olderThan1 });
    }
  };

  componentDidMount() {
    this.initialData();
    axios.get(`${origin}/cats`).then((response) => {
      this.setState({ catsList: response.data });
    });

    let read = JSON.parse(localStorage.getItem("ageFilteredList"));

    if (read) {
      this.setState({
        filterData: read,
      });
    }
  }

  onAgeChanged = (e) => {
    if (e.target.value === "olderThan1") {
      var filteredData = this.state.catsList.filter((data) => {
        return data.age >= 1;
      });
    }

    if (e.target.value === "lessThan1") {
      var filteredData = this.state.catsList.filter((data) => {
        return data.age === "Less than 1 ";
      });
    }

    localStorage.setItem("ageFilteredList", JSON.stringify(filteredData));

    this.setState({
      ...this.state,
      filterData: filteredData,
    });
  };

  render() {
    return (
      <div className="catss">
        <section className="catss__bottom">
          <div className="catss__row">
            <h3 className="catss__ages catss__ages--feature">
              <span className="catss__nums"> No. </span>
              <span className="catss__num">{this.state.filterData.length}</span>
            </h3>
          </div>
          <div className="catss__row">
            <h3 className="catss__ages" htmlFor="olderThan1">
              Years old
            </h3>
            <input
              defaultChecked={this.state.age === "olderThan1"}
              type="radio"
              name="age"
              id="olderThan1"
              value="olderThan1"
              onChange={(e) => {
                this.onAgeChanged(e);
              }}
            />
          </div>
          <div className="catss__row">
            <h3 className="catss__ages" htmlFor="lessThan1">
              Months old
            </h3>
            <input
              defaultChecked={this.state.age === "lessThan1"}
              type="radio"
              name="age"
              id="lessThan1"
              value="lessThan1"
              onChange={(e) => {
                this.onAgeChanged(e);
              }}
            />
          </div>
        </section>

        <ul className="catss__cats">
          {this.state.filterData.map((cat) => {
            return (
              <li className="cats__cat" key={cat.id}>
                <Cat
                  id={cat.id}
                  name={cat.name}
                  image={cat.image}
                  age={cat.age}
                  getData={this.chosenCat}
                />
              </li>
            );
          })}
        </ul>
        <CatsToBeAdopted />
      </div>
    );
  }
}
export default Cats;
