import { Link } from "react-router-dom";
import { Component } from "react";
import axios from "axios";
import Icon from "../../assets/icon/left.png";
import IconRight from "../../assets/icon/right.png";
import Cat from "../../Components/Cat/Cat";
import CatsToBeAdopted from "../CatToBeAdopted/CatsToBeAdopted";
import "./Cats.scss";

const origin = "http://localhost:9001";

class Cats extends Component {
  state = {
    catsList: [],
    //chosenCat: {},
  };

  // getChosenCatId() {
  //   if (this.props.match) {
  //     // if match exist in props, it hits /Chosen/:id
  //     return this.props.match.params.id;
  //   }
  // }

  componentDidMount() {
    axios.get(`${origin}/cats`).then((response) => {
      this.setState({ catsList: response.data });
      //console.log(response.data);
      // const id = this.getChosenCatId();
      // axios.get(`${origin}/cats/${id}`).then(({ data }) => {
      //   console.log(data.id);
      //   this.setState({ chosenCat: data });
      // });
    });
  }
  chosenCat = (chosenId) => {
    console.log("hello, the data id is :", chosenId);
    return chosenId;
  };

  render() {
    const { catsList } = this.state;

    // const newCatsList = this.state.catsList.filter((cat) => {
    //   return cat.id !== this.deleteCat;
    // });
    // console.log("hello", newCatsList);

    return (
      <div className="catss">
        <div className="catss__nav">
          <Link to={`/`} className="catss__link">
            <img src={Icon} className="catss__icon" />
            Back
          </Link>
          <Link to={`/cats/adopted`} className="catss__link">
            <img src={Icon} className="catss__icon" />
            adopted
          </Link>
          <Link to={`/cats/add`} className="catss__link">
            Upload
            <img src={IconRight} className="catss__icon" />
          </Link>
        </div>

        <ul className="catss__cats">
          {catsList.map((cat) => {
            return (
              <li className="cats__cat" key={cat.id}>
                <Cat
                  id={cat.id}
                  name={cat.name}
                  image={cat.image}
                  age={cat.age}
                  getData={this.chosenCat}
                />
              </li>
            );
          })}
        </ul>
        <CatsToBeAdopted getChosenCat={chosenCat} />
      </div>
    );
  }
}
export default Cats;
