import axios from "axios";
import { Component } from "react";
import { Link } from "react-router-dom";
import "./CatAdd.scss";

const origin = "http://localhost:9001";

class CatAdd extends Component {
  state = {
    catName: "",
    age: "",
    gender: "",
    breed: "",
    health: "",
    personality: "",
    city: "",
    contact: "",
  };

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
    console.log(this.state);
  };

  submitHandler = (e) => {
    e.preventDefault();

    axios
      .post(`${origin}/cats`, this.state)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    const { catName, age, gender, breed, health, personality, city, contact } =
      this.state;
    return (
      <div className="add">
        <form onSubmit={this.submitHandler} className="add__form">
          <div className="add__labels">
            <h3>Name</h3>
            <input
              type="text"
              name="catName"
              value={catName}
              placeholder="Please add Meow's name"
              onChange={this.changeHandler}
              className="add__input"
            />
          </div>

          <div className="add__labels">
            <h3>Age</h3>
            <input
              type="text"
              name="age"
              value={age}
              placeholder="How old is he/she "
              onChange={this.changeHandler}
              className="add__input"
            />
          </div>

          <div className="add__labels">
            <h3>Breed</h3>
            <input
              type="text"
              name="breed"
              value={breed}
              placeholder="Please add the breed"
              onChange={this.changeHandler}
              className="add__input"
            />
          </div>

          <div className="add__labels">
            <h3>Personality</h3>
            <input
              type="text"
              name="personality"
              value={personality}
              placeholder="What's the personality like "
              onChange={this.changeHandler}
              className="add__input"
            />
          </div>

          <div className="add__labels">
            <h3>Health</h3>
            <input
              type="text"
              name="health"
              value={health}
              onChange={this.changeHandler}
              placeholder="Please discribe the health "
              className="add__input"
            />
          </div>

          <div className="add__labels">
            <h3>Gender</h3>
            <input
              type="text"
              name="gender"
              value={gender}
              onChange={this.changeHandler}
              placeholder="Boy or Girl "
              className="add__input"
            />
          </div>

          <div className="add__labels">
            <h3>City</h3>
            <input
              type="text"
              name="city"
              value={city}
              onChange={this.changeHandler}
              placeholder="What's the city name "
              className="add__input"
            />
          </div>

          <div className="add__labels">
            <h3>Contact</h3>
            <input
              type="text"
              name="contact"
              value={contact}
              onChange={this.changeHandler}
              placeholder="Please add your email address"
              className="add__input"
            />
          </div>

          <button type="submit" className="add__btn">
            <Link to={`/cats`} className="add__link">
              Upload Meow
            </Link>
          </button>
        </form>
      </div>
    );
  }
}
export default CatAdd;
