import { Link } from "react-router-dom";
import { Component } from "react";
import axios from "axios";
import Icon from "../../assets/icon/left.png";
import IconRight from "../../assets/icon/right.png";
import Cat from "../../Components/Cat/Cat";
import CatsToBeAdopted from "../CatToBeAdopted/CatsToBeAdopted";
import "./Cats.scss";

const origin = "http://localhost:9001";

class Cats extends Component {
  state = {
    catsList: [],
    filterData: [],
  };

  filterData = (e) => {
    if (e.target.value === "male") {
      const filteredData = this.state.catsList.filter((data) => {
        return data.gender === "male";
      });
    } else if (e.target.value === "female") {
      const filteredData = this.state.catsList.filter((data) => {
        return data.gender === "female";
      });
    }

    this.setState({
      ...this.state,
      filterData: this.filteredData,
    });
  };

  componentDidMount() {
    axios.get(`${origin}/cats`).then((response) => {
      this.setState({ catsList: response.data });
    });
  }

  render() {
    const { catsList } = this.state;

    return (
      <div className="catss">
        <h4>Total Meows: {catsList.length}</h4>

        <section>
          <label htmlFor="male">Male</label>
          <input type="radio" id="male" value="male" />

          <label htmlFor="female">Female</label>
          <input type="radio" id="female" value="female" />
        </section>

        <div className="catss__nav">
          <Link to={`/`} className="catss__link">
            <img src={Icon} className="catss__icon" />
            Back
          </Link>

          <Link to={`/cats/adopted`} className="catss__link">
            <img src={Icon} className="catss__icon" />
            adopted
          </Link>
          <Link to={`/cats/add`} className="catss__link">
            Upload
            <img src={IconRight} className="catss__icon" />
          </Link>
        </div>

        <ul className="catss__cats">
          {catsList.map((cat) => {
            return (
              <li className="cats__cat" key={cat.id}>
                <Cat
                  id={cat.id}
                  name={cat.name}
                  image={cat.image}
                  age={cat.age}
                  getData={this.chosenCat}
                />
              </li>
            );
          })}
        </ul>
        <CatsToBeAdopted />
      </div>
    );
  }
}
export default Cats;
