  // const submitHandler = (e) => {
  //   e.preventDefault();

  //   axios
  //     .post(`${origin}/cats`, {
  //       name: e.target.catName,
  //       age: e.target.age,
  //       gender: e.target.gender,
  //       health: e.target.health,
  //       personality: e.target.personality,
  //       contact: e.target.contact,
  //       breed: e.target.breed,
  //       city: e.target.city,
  //       image: uploadImageURL,
  //     })
  //     .then(
  //       (response) => {
  //         console.log(response.data);
  //         // only navigate to the newly uploaded video detail page when successfully uploaded
  //         history.push(`/cats/${response.data.id}`);
  //         alert("Meow uploaded successfully");
  //       },
  //       (error) => {
  //         console.log(error.message);
  //         alert(error.message);
  //       }
  //     );

  return (
    <div className="add">
      <form onSubmit={submitHandler} className="add__form">
        <div className="add__labels">
          <h3>Name</h3>
          <input
            type="text"
            name="catName"
            placeholder="Please add Meow's name"
            className="add__input"
          />
        </div>

        <div className="add__labels">
          <h3>Age</h3>
          <input
            type="text"
            name="age"
            placeholder="How old is he/she "
            className="add__input"
          />
        </div>

        <div className="add__labels">
          <h3>Breed</h3>
          <input
            type="text"
            name="breed"
            placeholder="Please add the breed"
            className="add__input"
          />
        </div>

        <div className="add__labels">
          <h3>Personality</h3>
          <input
            type="text"
            name="personality"
            placeholder="What's the personality like "
            className="add__input"
          />
        </div>

        <div className="add__labels">
          <h3>Health</h3>
          <input
            type="text"
            name="health"
            placeholder="Please discribe the health "
            className="add__input"
          />
        </div>

        <div className="add__labels">
          <h3>Gender</h3>
          <input
            type="text"
            name="gender"
            placeholder="Boy or Girl "
            className="add__input"
          />
        </div>

        <div className="add__labels">
          <h3>City</h3>
          <input
            type="text"
            name="city"
            placeholder="What's the city name "
            className="add__input"
          />
        </div>

        <div className="add__labels">
          <h3>Contact</h3>
          <input
            type="text"
            name="contact"
            placeholder="Please add your email address"
            className="add__input"
          />
        </div>

        <button type="submit" className="add__btn">
          Upload Meow
        </button>
      </form>
    </div>
  );
}
export default CatAdd;
