import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import "./CatAdd.scss";

const origin = "http://localhost:9001";

function CatAdd() {
  const [data, setData] = useState({
    name: "",
    age: "",
    gender: "",
    health: "",
    personality: "",
    email: "",
    breed: "",
    city: "",
  });

  const inputHandler = (e) => {
    const newData = { ...data };
    newData[e.target.name] = e.target.value;
    setData(newData);
  };

  const submitHandler = (e) => {
    e.preventDefault();

    axios
      .post(`${origin}/cats`, {
        name: data.name,
        age: data.age,
        gender: data.gender,
        health: data.health,
        personality: data.personality,
        email: data.email,
        breed: data.breed,
        city: data.city,
      })
      .then((response) => {
        console.log(response.data);
      });
  };

  return (
    <div className="add">
      <form onSubmit={submitHandler} className="add__form">
        <div className="add__labels">
          <h3>Name</h3>
          <input
            value={data.name}
            type="text"
            name="name"
            placeholder="Please add Meow's name"
            className={`add__input ${
              data.name === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <h3>Age</h3>
          <input
            value={data.age}
            type="text"
            name="age"
            placeholder="How old is he/she "
            className={`add__input ${
              data.age === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <h3>Breed</h3>
          <input
            value={data.breed}
            type="text"
            name="breed"
            placeholder="Please add the breed"
            className={`add__input ${
              data.breed === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <h3>Personality</h3>
          <input
            value={data.personality}
            type="text"
            name="personality"
            placeholder="What's the personality like "
            className={`add__input ${
              data.personality === ""
                ? "add__input--error"
                : "add__input--success"
            }`}
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <h3>Health</h3>
          <input
            value={data.health}
            type="text"
            name="health"
            placeholder="Please discribe the health "
            className={`add__input ${
              data.health === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <h3>Gender</h3>
          <input
            value={data.gender}
            type="text"
            name="gender"
            placeholder="Boy or Girl "
            className={`add__input ${
              data.gender === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <h3>City</h3>
          <input
            value={data.city}
            type="text"
            name="city"
            placeholder="What's the city name "
            className={`add__input ${
              data.city === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            required
          />
        </div>

        <div className="add__labels">
          <h3>email</h3>
          <input
            value={data.email}
            type="email"
            name="email"
            placeholder="Please add your email address"
            className={`add__input ${
              data.email === "" ? "add__input--error" : "add__input--success"
            }`}
            onChange={inputHandler}
            required
          />
        </div>

        <button type="submit" className="add__btn">
          <Link className="add__link" to="/cats">
            Upload Meow
          </Link>
        </button>
      </form>
    </div>
  );
}
export default CatAdd;
